import React, { useState } from 'react'

export default function Uppercase(props) {
    const [text,setText] = useState("abc")

    let convertupper = ()=>{

        setText(text.toUpperCase())
    }
    let textchange = (event)=>{
        setText(event.tar)
    }

  return (
    <>
        <h1>Welcome</h1>
        <div>

        <textarea name="text-area" id="text-area" className='border-2 border-gray-400' onChange={textchange}> </textarea>
        </div>
        <button className='border-2 bg-blue-600 text-white p-2 rounded-xl' onClick={convertupper}>Convert to UpperCase</button>

    </>
  )
}
