import React, { useState } from 'react'

export default function CaseChanger() {
    const [mode, setMode] = useState(second)
    const [text,setText] = useState("")

    let convertupper = ()=>{
        let newtext = text.toUpperCase()
        setText(newtext)
    }
    let convertlower = ()=>{
        let newtext = text.toLowerCase()
        setText(newtext)
    }
    let textchange = (event)=>{
        setText(event.target.value)
    }

  return (
    <>
    <div className='flex flex-col justify-center items-center w-screen h-screen'>
        <div className='w-4/5 h-4/5 pb-4'>
        <textarea name="text-area" id="text-area" className='border-2 border-gray-400 w-full h-full p-4' value={text} onChange={textchange}> </textarea>
        </div>
        <div>

        <button className='border-2 bg-blue-600 text-white p-2 rounded-xl ' onClick={convertupper}>Convert to UpperCase</button>
        <button className='border-2 bg-blue-600 text-white p-2 rounded-xl ' onClick={convertlower}>Convert to LowerCase</button>
        </div>
        <div className='pt-3'>
            Words {text.split(" ").length} , Characters {text.length} , WhiteSpaces {(text.match(/\s/g) || []).length}
        </div>
    </div>


    </>
  )
}
