import './App.css';
import Data from "./Components/CaseChanger"
import { Switch } from '@mui/material';
import { useState } from 'react';

function App() {
  const [mode, setMode] = useState({
          color:"black",
          background:"white"

      })
      const [modetext, setModetext] = useState("Enable Dark Mode")
      const togglemode = ()=>{
          if (mode.color === "black"){
              setMode({color:"white",
                  background:"black",

              })
              setModetext(("Enable Light Mode"))
          }
          else{
              setMode({color:"black",
                  background:"white",})
              setModetext(("Enable Dark Mode"))
          }
      }
  const label = { inputProps: { 'aria-label': 'Switch demo' } };
  return (
    <div className='h-screen ' style={mode}>
      <div className='text-right pr-4 pt-4' style={mode}>
      <Switch className='border-gray-400 border-2 rounded-3xl' onClick={togglemode} {...label}  color="default"/>
      </div>
      <Data mode={mode} modetext={modetext} togglemode={togglemode}/>
    </div>
  );
}

export default App;
