import React, { useState } from 'react'

export default function Uppercase() {
    const [text,setText] = useState("Dummy Text")

    let convertupper = ()=>{
        let newtext = text.toUpperCase()
        setText(newtext)
    }
    let convertlower = ()=>{
        let newtext = text.toLowerCase()
        setText(newtext)
    }
    let textchange = (event)=>{
        setText(event.target.value)
    }

  return (
    <>
    <div className='flex flex-col justify-center items-center w-screen h-screen'>
        <div className='w-4/5 h'>
        <textarea name="text-area" id="text-area" className='border-2 border-gray-400' value={text} onChange={textchange}> </textarea>
        </div>
        <div>

        <button className='border-2 bg-blue-600 text-white p-2 rounded-xl ' onClick={convertupper}>Convert to UpperCase</button>
        <button className='border-2 bg-blue-600 text-white p-2 rounded-xl ' onClick={convertlower}>Convert to LowerCase</button>
        </div>
    </div>


    </>
  )
}
