import React, { useState } from 'react'

export default function CaseChanger(props) {

        const [modetext, setModetext] = useState("Enable Dark Mode")
        const [text,setText] = useState("")

        let convertupper = ()=>{
            let newtext = text.toUpperCase()
            setText(newtext)
        }
        let convertlower = ()=>{
            let newtext = text.toLowerCase()
            setText(newtext)
        }
        let textchange = (event)=>{
            setText(event.target.value)
        }
        const togglemode = ()=>{
            if (mode.color === "black"){
                setMode({color:"white",
                    background:"black",

                })
                setModetext(("Enable Light Mode"))
            }
            else{
                setMode({color:"black",
                    background:"white",})
                setModetext(("Enable Dark Mode"))
            }
        }

  return (
    <>
    <div className='text-right pb-12' style={props.mode}>
        <button type='button' onClick={togglemode} style={mode}  className='mr-4 mt-4   border-2 rounded-xl p-2'>{modetext}</button>
    </div>
    <div className='flex flex-col  items-center w-screen h-screen' style={props.mode}>
        <div className='w-4/5 h-3/5 pb-4'>
        <textarea name="text-area" id="text-area" className='border-2 border-gray-400 w-full h-full p-4' style={mode} value={text} onChange={textchange}> </textarea>
        </div>
        <div>

        <button className='border-2 bg-blue-200 mx-2 text-white p-2 rounded-xl ' style={props.mode} onClick={convertupper}>Convert to UpperCase</button>
        <button className='border-2 bg-blue-200 mx-2 text-white p-2 rounded-xl ' style={mode} onClick={convertlower}>Convert to LowerCase</button>
        </div>
        <div className='pt-3'>
            <div style={mode}>
            Words {text.split(" ").length} , Characters {text.length} , WhiteSpaces {(text.match(/\s/g) || []).length}
            </div>
        </div>
    </div>


    </>
  )
}
