import React, { useState } from 'react'

export default function CaseChanger(props) {
    const [text,setText] = useState("")


        let convertupper = ()=>{
            let newtext = text.toUpperCase()
            setText(newtext)
        }
        let convertlower = ()=>{
            let newtext = text.toLowerCase()
            setText(newtext)
        }
        let textchange = (event)=>{
            setText(event.target.value)
        }

  return (
    <>
    {/* <div className='text-right pb-12' style={props.mode}>
        <button type='button' onClick={props.togglemode} style={props.mode}  className='mr-4 mt-4   border-2 rounded-xl p-2'>{props.modetext}</button>
    </div> */}
    <div className='flex flex-col  items-center   w-screen' style={props.mode}>
        <div className='pb-4 text-center  w-2/3  flex justify-center items-end mb-4'>
        <textarea name="text-area" id="text-area" className='border-2 border-gray-400 w-2/3 p-4' style={props.mode} value={text} onChange={textchange}> </textarea>
        </div>
        <div >

        <button className='border-2 bg-blue-200 mx-2 text-white p-2 rounded-xl ' style={props.mode} onClick={convertupper}>Convert to UpperCase</button>
        <button className='border-2 bg-blue-200 mx-2 text-white p-2 rounded-xl ' style={props.mode} onClick={convertlower}>Convert to LowerCase</button>
        </div>
        <div className='pt-3'>
            <div style={props.mode}>
            <p>Words {text.split(" ").length} , Characters {text.length} , WhiteSpaces {(text.match(/\s/g) || []).length}</p>
            </div>
        </div>
    </div>


    </>
  )
}
